import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import Draggable from 'react-draggable';
import { Sketch } from '@uiw/react-color';
import { Search, Upload, Download, X, Move, Image as ImageIcon, Shapes } from 'lucide-react';
import { sampleSvgShapes } from './sampleSvgData';

const CANVAS_WIDTH = 1200;
const CANVAS_HEIGHT = 600;
const MAX_ELEMENT_SIZE = 600;

function App() {
  const [mode, setMode] = useState('shapes'); // 'shapes' or 'upload'
  const [selectedShape, setSelectedShape] = useState(null);
  const [uploadedImage, setUploadedImage] = useState(null);
  const [backgroundColor, setBackgroundColor] = useState('transparent');
  const [svgColor, setSvgColor] = useState('#000000');
  const [showBgColorPicker, setShowBgColorPicker] = useState(false);
  const [showSvgColorPicker, setShowSvgColorPicker] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [elementPosition, setElementPosition] = useState({ x: 0, y: 0 });
  const [showPreview, setShowPreview] = useState(false);
  const [svgShapes, setSvgShapes] = useState(sampleSvgShapes);
  const [hoveredShape, setHoveredShape] = useState(null);

  const canvasRef = useRef(null);
  const previewCanvasRef = useRef(null);
  const dragNodeRef = useRef(null);



  // Calculate element dimensions for 600px height
  const calculateElementDimensions = (originalWidth, originalHeight) => {
    const maxHeight = 600;
    const maxWidth = 600;

    let width = originalWidth;
    let height = originalHeight;

    // Scale down if height exceeds maxHeight
    if (height > maxHeight) {
      const ratio = maxHeight / height;
      width *= ratio;
      height = maxHeight;
    }

    // Scale down if width exceeds maxWidth
    if (width > maxWidth) {
      const ratio = maxWidth / width;
      height *= ratio;
      width = maxWidth;
    }

    return { width, height };
  };

  // Calculate center position for element
  const calculateCenterPosition = (elementWidth, elementHeight) => {
    const canvasWidth = CANVAS_WIDTH;
    const canvasHeight = CANVAS_HEIGHT;

    const x = (canvasWidth - elementWidth) / 2;
    const y = (canvasHeight - elementHeight) / 2;

    return { x, y };
  };

  // Handle drag end
  const handleDragEnd = (e, data) => {
    setElementPosition({ x: data.x, y: data.y });
  };

  // Generate tags from filename
  const generateTags = (filename) => {
    const name = filename.replace(/\.[^/.]+$/, ''); // Remove extension
    const tags = name.split(/[-_\s]+/).filter(tag => tag.length > 0);
    return tags.map(tag => tag.toLowerCase());
  };

  // Filter shapes based on search
  const filteredShapes = svgShapes.filter(shape => {
    if (!searchTerm) return true;
    const searchLower = searchTerm.toLowerCase();
    return shape.name.toLowerCase().includes(searchLower) ||
           shape.tags.some(tag => tag.includes(searchLower));
  });

  // Handle file drop for images
  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const img = new Image();
      img.onload = () => {
        // Calculate resize dimensions
        let width = img.width;
        let height = img.height;
        const maxSize = MAX_ELEMENT_SIZE;

        if (width > maxSize || height > maxSize) {
          const ratio = Math.min(maxSize / width, maxSize / height);
          width *= ratio;
          height *= ratio;
        }

        setUploadedImage({
          src: e.target.result,
          width,
          height,
          originalWidth: img.width,
          originalHeight: img.height,
          name: file.name
        });
        setMode('upload');
        setSelectedShape(null);
      };
      img.src = e.target.result;
    };
    reader.readAsDataURL(file);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.png', '.jpg', '.jpeg', '.svg']
    },
    multiple: false
  });

  // Handle SVG file upload for shapes library
  const handleSvgLibraryUpload = (e) => {
    const files = Array.from(e.target.files);

    files.forEach(file => {
      if (file.type === 'image/svg+xml' || file.name.endsWith('.svg')) {
        const reader = new FileReader();
        reader.onload = (event) => {
          const newShape = {
            id: Date.now() + Math.random(),
            name: file.name.replace('.svg', ''),
            svg: event.target.result,
            tags: generateTags(file.name)
          };
          setSvgShapes(prev => [...prev, newShape]);
        };
        reader.readAsText(file);
      }
    });
  };

  // Draw on canvas
  const drawCanvas = useCallback((canvas, context) => {
    if (!canvas || !context) return;

    // Clear canvas
    context.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

    // Draw background
    if (backgroundColor !== 'transparent') {
      context.fillStyle = backgroundColor;
      context.fillRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    }

    // Draw element (shape or image)
    if (mode === 'shapes' && selectedShape) {
      drawSvgOnCanvas(context, selectedShape.svg, elementPosition.x, elementPosition.y, svgColor);
    } else if (mode === 'upload' && uploadedImage) {
      const img = new Image();
      img.onload = () => {
        context.drawImage(img, elementPosition.x, elementPosition.y, uploadedImage.width, uploadedImage.height);
      };
      img.src = uploadedImage.src;
    }
  }, [backgroundColor, mode, selectedShape, uploadedImage, elementPosition, svgColor]);

  // Draw SVG on canvas with proper dimensions
  const getElementDimensions = () => {
    if (mode === 'shapes' && selectedShape) {
      // For SVGs, we'll use a standard size and let the canvas drawing handle the scaling
      return { width: 600, height: 600 }; // Use 600px as the target size
    } else if (mode === 'upload' && uploadedImage) {
      return { width: uploadedImage.width, height: uploadedImage.height };
    }
    return { width: 0, height: 0 };
  };

  // Get the actual display dimensions for positioning
  const getDisplayDimensions = () => {
    if (mode === 'shapes' && selectedShape) {
      // For SVGs, we want 600px height, width calculated to maintain aspect ratio
      // Since we don't know the original SVG dimensions, we'll use a square format
      return { width: 600, height: 600 };
    } else if (mode === 'upload' && uploadedImage) {
      return { width: uploadedImage.width, height: uploadedImage.height };
    }
    return { width: 0, height: 0 };
  };

  // Draw SVG on canvas
  const drawSvgOnCanvas = (context, svgString, x, y, color) => {
    const img = new Image();
    const svg = new Blob([svgString.replace(/fill="[^"]*"/g, `fill="${color}"`)], { type: 'image/svg+xml' });
    const url = URL.createObjectURL(svg);

    img.onload = () => {
      // For SVGs, we want to draw them at 600px height, maintaining aspect ratio
      const targetHeight = 600;
      const targetWidth = 600; // We'll use square format for SVGs

      // Draw the SVG at the target size
      context.drawImage(img, x, y, targetWidth, targetHeight);
      URL.revokeObjectURL(url);
    };
    img.src = url;
  };

  // Update canvas whenever dependencies change
  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas?.getContext('2d');
    drawCanvas(canvas, context);
  }, [drawCanvas]);

  // Export as PNG
  const exportAsPNG = () => {
    const canvas = canvasRef.current;
    canvas.toBlob((blob) => {
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `push-notification-${selectedShape?.name || uploadedImage?.name || 'image'}.png`;
      a.click();
      URL.revokeObjectURL(url);
    });
  };

  // Show preview
  const handleShowPreview = () => {
    setShowPreview(true);
    setTimeout(() => {
      const canvas = previewCanvasRef.current;
      const context = canvas?.getContext('2d');
      drawCanvas(canvas, context);
    }, 100);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto p-6 max-w-7xl">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">Push Notification Image Generator</h1>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left Panel - Shape/Image Selection */}
          <div className="lg:col-span-1 space-y-4">
            {/* Mode Selector */}
            <div className="bg-white rounded-lg shadow p-4">
              <div className="flex gap-2">
                <button
                  onClick={() => setMode('shapes')}
                  className={`flex-1 py-2 px-4 rounded-lg font-medium transition-colors flex items-center justify-center gap-2 ${
                    mode === 'shapes' 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  <Shapes size={20} />
                  SVG Shapes
                </button>
                <button
                  onClick={() => setMode('upload')}
                  className={`flex-1 py-2 px-4 rounded-lg font-medium transition-colors flex items-center justify-center gap-2 ${
                    mode === 'upload' 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  <ImageIcon size={20} />
                  Upload Image
                </button>
              </div>
            </div>

            {/* Shapes Mode */}
            {mode === 'shapes' && (
              <div className="bg-white rounded-lg shadow p-4 space-y-4">
                <div className="flex items-center justify-between">
                  <h2 className="text-lg font-semibold">SVG Shapes</h2>
                  <label className="cursor-pointer bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600">
                    Add SVGs
                    <input
                      type="file"
                      multiple
                      accept=".svg"
                      onChange={handleSvgLibraryUpload}
                      className="hidden"
                    />
                  </label>
                </div>

                {/* Search */}
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                  <input
                    type="text"
                    placeholder="Search shapes..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                {/* Shapes Grid */}
                <div className="grid grid-cols-3 gap-3 max-h-96 overflow-y-auto">
                  {filteredShapes.map((shape) => (
                    <div
                      key={shape.id}
                      onClick={() => {
                        setSelectedShape(shape);
                        // Center the element with 600px height
                        const { width, height } = getDisplayDimensions();
                        const { x, y } = calculateCenterPosition(width, height);
                        setElementPosition({ x, y });
                      }}
                      onMouseEnter={() => setHoveredShape(shape)}
                      onMouseLeave={() => setHoveredShape(null)}
                      className={`relative border-2 rounded-lg p-4 cursor-pointer transition-all ${
                        selectedShape?.id === shape.id 
                          ? 'border-blue-500 bg-blue-50' 
                          : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
                      }`}
                    >
                      <div 
                        className="w-full h-20 flex items-center justify-center"
                        dangerouslySetInnerHTML={{ __html: shape.svg }}
                      />
                      <p className="text-xs text-center mt-2 truncate">{shape.name}</p>

                      {/* Hover Preview */}
                      {hoveredShape?.id === shape.id && (
                        <div className="absolute z-10 -top-2 -right-2 bg-white rounded-lg shadow-lg p-2 pointer-events-none">
                          <div 
                            className="w-32 h-32"
                            dangerouslySetInnerHTML={{ __html: shape.svg }}
                          />
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Upload Mode */}
            {mode === 'upload' && (
              <div className="bg-white rounded-lg shadow p-4">
                <h2 className="text-lg font-semibold mb-4">Upload Image</h2>
                <div {...getRootProps()} className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center cursor-pointer hover:border-gray-400 transition-colors">
                  <input {...getInputProps()} />
                  <Upload className="mx-auto mb-4 text-gray-400" size={48} />
                  {isDragActive ? (
                    <p className="text-gray-600">Drop the image here...</p>
                  ) : (
                    <>
                      <p className="text-gray-600 mb-2">Drag & drop an image here, or click to select</p>
                      <p className="text-sm text-gray-500">Supports: JPG, PNG, SVG (max 600x600)</p>
                    </>
                  )}
                </div>

                {uploadedImage && (
                  <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                    <p className="text-sm font-medium mb-2">{uploadedImage.name}</p>
                    <p className="text-xs text-gray-500">
                      Original: {uploadedImage.originalWidth}x{uploadedImage.originalHeight}
                    </p>
                    <p className="text-xs text-gray-500">
                      Resized: {Math.round(uploadedImage.width)}x{Math.round(uploadedImage.height)}
                    </p>
                  </div>
                )}
              </div>
            )}

            {/* Color Controls */}
            <div className="bg-white rounded-lg shadow p-4 space-y-4">
              <h2 className="text-lg font-semibold">Colors</h2>

              {/* Background Color */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Background Color</label>
                <div className="flex gap-2">
                  <button
                    onClick={() => setBackgroundColor('transparent')}
                    className={`px-3 py-1 rounded text-sm ${
                      backgroundColor === 'transparent' 
                        ? 'bg-gray-800 text-white' 
                        : 'bg-gray-200 text-gray-700'
                    }`}
                  >
                    Transparent
                  </button>
                  <div className="relative">
                    <button
                      onClick={() => setShowBgColorPicker(!showBgColorPicker)}
                      className="w-10 h-10 rounded border-2 border-gray-300"
                      style={{ backgroundColor: backgroundColor === 'transparent' ? '#fff' : backgroundColor }}
                    />
                    {showBgColorPicker && (
                      <div className="absolute z-10 mt-2">
                        <div className="fixed inset-0" onClick={() => setShowBgColorPicker(false)} />
                        <Sketch
                          color={backgroundColor === 'transparent' ? '#ffffff' : backgroundColor}
                          onChange={(color) => setBackgroundColor(color.hex)}
                        />
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* SVG Color */}
              {mode === 'shapes' && selectedShape && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Shape Color</label>
                  <div className="relative">
                    <button
                      onClick={() => setShowSvgColorPicker(!showSvgColorPicker)}
                      className="w-full h-10 rounded border-2 border-gray-300"
                      style={{ backgroundColor: svgColor }}
                    />
                    {showSvgColorPicker && (
                      <div className="absolute z-10 mt-2">
                        <div className="fixed inset-0" onClick={() => setShowSvgColorPicker(false)} />
                        <Sketch
                          color={svgColor}
                          onChange={(color) => setSvgColor(color.hex)}
                        />
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Right Panel - Canvas */}
          <div className="lg:col-span-2 space-y-4">
            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-semibold">Canvas Preview</h2>
                <div className="flex items-center gap-2 text-sm text-gray-500">
                  <Move size={16} />
                  Drag to position
                </div>
              </div>

              {/* Canvas Container */}
              <div className="relative border-2 border-gray-300 rounded-lg overflow-hidden" 
                   style={{ width: '100%', maxWidth: `${CANVAS_WIDTH/2}px`, margin: '0 auto' }}>
                <canvas
                  ref={canvasRef}
                  width={CANVAS_WIDTH}
                  height={CANVAS_HEIGHT}
                  className="w-full h-auto"
                  style={{ backgroundColor: backgroundColor === 'transparent' ? '#f3f4f6' : backgroundColor }}
                />

                {/* Element Position Indicator */}
                {(selectedShape || uploadedImage) && (
                  <div 
                    className="absolute border-2 border-blue-500 border-dashed bg-blue-100 bg-opacity-20 cursor-move"
                    style={{ 
                      left: `${elementPosition.x/2}px`,
                      top: `${elementPosition.y/2}px`,
                      width: `${(mode === 'shapes' ? 600 : uploadedImage.width)/2}px`,
                      height: `${(mode === 'shapes' ? 600 : uploadedImage.height)/2}px`,
                    }}
                    onMouseDown={(e) => {
                      e.preventDefault();
                      const startX = e.clientX;
                      const startY = e.clientY;
                      const startPos = elementPosition;

                      const handleMouseMove = (e) => {
                        const deltaX = (e.clientX - startX) * 2;
                        const deltaY = (e.clientY - startY) * 2;
                        setElementPosition({
                          x: Math.max(0, Math.min(CANVAS_WIDTH - (mode === 'shapes' ? 600 : uploadedImage.width), startPos.x + deltaX)),
                          y: Math.max(0, Math.min(CANVAS_HEIGHT - (mode === 'shapes' ? 600 : uploadedImage.height), startPos.y + deltaY))
                        });
                      };

                      const handleMouseUp = () => {
                        document.removeEventListener('mousemove', handleMouseMove);
                        document.removeEventListener('mouseup', handleMouseUp);
                      };

                      document.addEventListener('mousemove', handleMouseMove);
                      document.addEventListener('mouseup', handleMouseUp);
                    }}
                  >
                    <div className="w-full h-full flex items-center justify-center text-blue-600 text-xs">
                      Drag to move
                    </div>
                  </div>
                )}
              </div>

              {/* Export Actions */}
              <div className="mt-6 flex justify-center gap-4">
                <button
                  onClick={handleShowPreview}
                  className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors"
                  disabled={!selectedShape && !uploadedImage}
                >
                  Preview
                </button>
                <button
                  onClick={exportAsPNG}
                  className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors flex items-center gap-2"
                  disabled={!selectedShape && !uploadedImage}
                >
                  <Download size={20} />
                  Download PNG
                </button>
              </div>
            </div>

            {/* Instructions */}
            <div className="bg-blue-50 rounded-lg p-4">
              <h3 className="font-semibold text-blue-900 mb-2">How to use:</h3>
              <ol className="text-sm text-blue-800 space-y-1 list-decimal list-inside">
                <li>Choose between SVG shapes or upload your own image</li>
                <li>Select or upload your desired element</li>
                <li>Drag the element to position it on the canvas</li>
                <li>Customize colors as needed</li>
                <li>Preview and download your 1200x600 PNG image</li>
              </ol>
            </div>
          </div>
        </div>
      </div>

      {/* Preview Modal */}
      {showPreview && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-2xl max-w-5xl w-full">
            <div className="flex items-center justify-between p-4 border-b">
              <h3 className="text-lg font-semibold">Preview</h3>
              <button
                onClick={() => setShowPreview(false)}
                className="p-1 hover:bg-gray-100 rounded"
              >
                <X size={24} />
              </button>
            </div>
            <div className="p-4">
              <canvas
                ref={previewCanvasRef}
                width={CANVAS_WIDTH}
                height={CANVAS_HEIGHT}
                className="w-full h-auto border border-gray-300 rounded"
              />
            </div>
            <div className="flex justify-end gap-4 p-4 border-t">
              <button
                onClick={() => setShowPreview(false)}
                className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300"
              >
                Close
              </button>
              <button
                onClick={exportAsPNG}
                className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 flex items-center gap-2"
              >
                <Download size={20} />
                Download PNG
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
