import React, { useState, useRef, useEffect, useCallback, useMemo } from 'react';
import { useDropzone } from 'react-dropzone';
import { Sketch } from '@uiw/react-color';
import { Upload, Download, Image as ImageIcon, Shapes, Palette, FolderOpen, Search } from 'lucide-react';
import { sampleSvgShapes } from './sampleSvgData';

const CANVAS_WIDTH = 1200;
const CANVAS_HEIGHT = 600;
const MAX_SIZE = 600;
const ICONS_PER_ROW = 3;
const ICON_HEIGHT = 120; // Height of each icon item including padding

function App() {
  const [mode, setMode] = useState('shapes');
  const [selectedShape, setSelectedShape] = useState(null);
  const [uploadedImage, setUploadedImage] = useState(null);
  const [backgroundColor, setBackgroundColor] = useState('#ffffff');
  const [svgColor, setSvgColor] = useState('#000000');
  const [showBgColorPicker, setShowBgColorPicker] = useState(false);
  const [showSvgColorPicker, setShowSvgColorPicker] = useState(false);
  const [elementPosition, setElementPosition] = useState({ x: 300, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [allShapes, setAllShapes] = useState(sampleSvgShapes);
  const [isLoadingIcons, setIsLoadingIcons] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [iconSource, setIconSource] = useState('builtin'); // 'builtin' or 'folder'

  const canvasRef = useRef(null);
  const canvasContainerRef = useRef(null);

  // Load icons from folder
  const loadIconsFromFolder = async () => {
    setIsLoadingIcons(true);
    try {
      const response = await fetch('/api/icons');
      const data = await response.json();

      if (data.icons && data.icons.length > 0) {
        setAllShapes(data.icons);
        setIconSource('folder');
      } else {
        alert('No icons found in the public/icons folder. Add SVG files there to use them.');
      }
    } catch (error) {
      console.error('Error loading icons:', error);
      alert('Failed to load icons from folder. Make sure to add SVG files to public/icons/');
    } finally {
      setIsLoadingIcons(false);
    }
  };

  // Filter shapes based on search
  const filteredShapes = allShapes.filter(shape => {
    if (!searchTerm) return true;
    const searchLower = searchTerm.toLowerCase();
    return shape.name.toLowerCase().includes(searchLower) ||
           shape.tags.some(tag => tag.toLowerCase().includes(searchLower));
  });

  // Calculate center position
  const calculateCenterPosition = (width, height) => {
    return {
      x: (CANVAS_WIDTH - width) / 2,
      y: (CANVAS_HEIGHT - height) / 2
    };
  };

  // Handle file drop
  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const img = new Image();
      img.onload = () => {
        let width = img.width;
        let height = img.height;

        if (height > MAX_SIZE) {
          const ratio = MAX_SIZE / height;
          width *= ratio;
          height = MAX_SIZE;
        }

        if (width > MAX_SIZE) {
          const ratio = MAX_SIZE / width;
          height *= ratio;
          width = MAX_SIZE;
        }

        const uploadedData = {
          src: e.target.result,
          width,
          height,
          originalWidth: img.width,
          originalHeight: img.height,
          name: file.name
        };

        setUploadedImage(uploadedData);
        setMode('upload');
        setSelectedShape(null);

        const { x, y } = calculateCenterPosition(width, height);
        setElementPosition({ x, y });
      };
      img.src = e.target.result;
    };
    reader.readAsDataURL(file);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: { 'image/*': ['.png', '.jpg', '.jpeg', '.svg'] },
    multiple: false
  });

  // Draw on canvas
  const drawCanvas = useCallback(() => {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext('2d');
    if (!canvas || !ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

    // Draw background
    ctx.fillStyle = backgroundColor;
    ctx.fillRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

    // Draw element
    if (mode === 'shapes' && selectedShape) {
      const img = new Image();
      const svg = new Blob([selectedShape.svg.replace(/fill="[^"]*"/g, `fill="${svgColor}"`)], { type: 'image/svg+xml' });
      const url = URL.createObjectURL(svg);

      img.onload = () => {
        ctx.drawImage(img, elementPosition.x, elementPosition.y, MAX_SIZE, MAX_SIZE);
        URL.revokeObjectURL(url);
      };
      img.src = url;
    } else if (mode === 'upload' && uploadedImage) {
      const img = new Image();
      img.onload = () => {
        ctx.drawImage(img, elementPosition.x, elementPosition.y, uploadedImage.width, uploadedImage.height);
      };
      img.src = uploadedImage.src;
    }
  }, [backgroundColor, mode, selectedShape, uploadedImage, elementPosition, svgColor]);

  // Update canvas when dependencies change
  useEffect(() => {
    drawCanvas();
  }, [drawCanvas]);

  // Handle mouse drag on canvas
  const handleCanvasMouseDown = (e) => {
    if (!selectedShape && !uploadedImage) return;

    const rect = canvasContainerRef.current.getBoundingClientRect();
    const scale = rect.width / CANVAS_WIDTH;
    const mouseX = (e.clientX - rect.left) / scale;
    const mouseY = (e.clientY - rect.top) / scale;

    const elementWidth = mode === 'shapes' ? MAX_SIZE : uploadedImage.width;
    const elementHeight = mode === 'shapes' ? MAX_SIZE : uploadedImage.height;

    // Check if click is within element bounds
    if (mouseX >= elementPosition.x && mouseX <= elementPosition.x + elementWidth &&
        mouseY >= elementPosition.y && mouseY <= elementPosition.y + elementHeight) {
      setIsDragging(true);

      const offsetX = mouseX - elementPosition.x;
      const offsetY = mouseY - elementPosition.y;

      const handleMouseMove = (e) => {
        const rect = canvasContainerRef.current.getBoundingClientRect();
        const scale = rect.width / CANVAS_WIDTH;
        const newX = (e.clientX - rect.left) / scale - offsetX;
        const newY = (e.clientY - rect.top) / scale - offsetY;

        setElementPosition({
          x: Math.max(0, Math.min(CANVAS_WIDTH - elementWidth, newX)),
          y: Math.max(0, Math.min(CANVAS_HEIGHT - elementHeight, newY))
        });
      };

      const handleMouseUp = () => {
        setIsDragging(false);
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };

      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }
  };

  // Export as PNG
  const exportAsPNG = () => {
    const canvas = canvasRef.current;
    canvas.toBlob((blob) => {
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `push-notification-${Date.now()}.png`;
      a.click();
      URL.revokeObjectURL(url);
    });
  };

  // Select shape
  const selectShape = (shape) => {
    setSelectedShape(shape);
    setMode('shapes');
    const { x, y } = calculateCenterPosition(MAX_SIZE, MAX_SIZE);
    setElementPosition({ x, y });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto p-4 sm:p-6">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-sm p-6 mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Push Notification Designer</h1>
          <p className="text-gray-600 mt-1">Create beautiful 1200×600 images for your notifications</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left Sidebar */}
          <div className="space-y-4">
            {/* Mode Selector */}
            <div className="bg-white rounded-xl shadow-sm p-4">
              <div className="flex gap-2">
                <button
                  onClick={() => setMode('shapes')}
                  className={`flex-1 py-3 px-4 rounded-lg font-medium transition-all flex items-center justify-center gap-2 ${
                    mode === 'shapes' 
                      ? 'bg-blue-500 text-white shadow-md' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  <Shapes size={18} />
                  Shapes
                </button>
                <button
                  onClick={() => setMode('upload')}
                  className={`flex-1 py-3 px-4 rounded-lg font-medium transition-all flex items-center justify-center gap-2 ${
                    mode === 'upload' 
                      ? 'bg-blue-500 text-white shadow-md' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  <ImageIcon size={18} />
                  Upload
                </button>
              </div>
            </div>

            {/* Content Area */}
            {mode === 'shapes' ? (
              <div className="bg-white rounded-xl shadow-sm p-4">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="font-semibold text-gray-900">Select a Shape</h3>
                  <button
                    onClick={loadIconsFromFolder}
                    disabled={isLoadingIcons}
                    className="text-sm px-3 py-1.5 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-lg transition-colors flex items-center gap-1.5 disabled:opacity-50"
                    title="Load icons from public/icons folder"
                  >
                    <FolderOpen size={16} />
                    {isLoadingIcons ? 'Loading...' : 'Load from Folder'}
                  </button>
                </div>

                {/* Search Bar */}
                <div className="relative mb-4">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={18} />
                  <input
                    type="text"
                    placeholder="Search icons..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm"
                  />
                </div>

                {/* Icon Info */}
                <div className="mb-3 text-xs text-gray-500">
                  {iconSource === 'folder' ? (
                    <p>Showing {filteredShapes.length} icons from folder</p>
                  ) : (
                    <p>Showing {filteredShapes.length} built-in icons</p>
                  )}
                </div>

                <div className="grid grid-cols-3 gap-2 max-h-96 overflow-y-auto pr-2">
                  {filteredShapes.map((shape) => (
                    <button
                      key={shape.id}
                      onClick={() => selectShape(shape)}
                      className={`p-4 rounded-lg border-2 transition-all hover:scale-105 ${
                        selectedShape?.id === shape.id 
                          ? 'border-blue-500 bg-blue-50' 
                          : 'border-gray-200 hover:border-gray-300 bg-white'
                      }`}
                      title={shape.name}
                    >
                      <div 
                        className="w-full h-16"
                        dangerouslySetInnerHTML={{ __html: shape.svg }}
                      />
                      <p className="text-xs mt-2 text-gray-600 truncate">{shape.name}</p>
                    </button>
                  ))}
                </div>

                {/* Instructions */}
                <div className="mt-4 p-3 bg-blue-50 rounded-lg">
                  <p className="text-xs text-blue-700">
                    <strong>Tip:</strong> Add SVG files to <code className="bg-blue-100 px-1 rounded">public/icons/</code> folder and click "Load from Folder" to use your own icons.
                  </p>
                </div>
              </div>
            ) : (
              <div className="bg-white rounded-xl shadow-sm p-4">
                <h3 className="font-semibold text-gray-900 mb-4">Upload Image</h3>
                <div 
                  {...getRootProps()} 
                  className={`border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-all ${
                    isDragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300 hover:border-gray-400'
                  }`}
                >
                  <input {...getInputProps()} />
                  <Upload className="mx-auto mb-3 text-gray-400" size={32} />
                  <p className="text-gray-600 font-medium">
                    {isDragActive ? 'Drop image here' : 'Drag & drop or click'}
                  </p>
                  <p className="text-sm text-gray-500 mt-1">PNG, JPG up to 600×600</p>
                </div>

                {uploadedImage && (
                  <div className="mt-4 p-3 bg-gray-50 rounded-lg">
                    <p className="text-sm font-medium text-gray-700 truncate">{uploadedImage.name}</p>
                    <p className="text-xs text-gray-500 mt-1">
                      {Math.round(uploadedImage.width)} × {Math.round(uploadedImage.height)}px
                    </p>
                  </div>
                )}
              </div>
            )}

            {/* Color Controls */}
            <div className="bg-white rounded-xl shadow-sm p-4">
              <h3 className="font-semibold text-gray-900 mb-4 flex items-center gap-2">
                <Palette size={18} />
                Colors
              </h3>

              {/* Background Color */}
              <div className="mb-4">
                <label className="text-sm font-medium text-gray-700 mb-2 block">Background</label>
                <div className="relative">
                  <button
                    onClick={() => setShowBgColorPicker(!showBgColorPicker)}
                    className="w-full h-12 rounded-lg border-2 border-gray-300 relative overflow-hidden group hover:border-gray-400 transition-colors"
                  >
                    <div className="absolute inset-0" style={{ backgroundColor }} />
                    <span className="relative z-10 text-sm font-medium mix-blend-difference text-white">
                      {backgroundColor}
                    </span>
                  </button>

                  {showBgColorPicker && (
                    <div className="absolute z-20 mt-2">
                      <div className="fixed inset-0" onClick={() => setShowBgColorPicker(false)} />
                      <div className="relative bg-white rounded-lg shadow-xl p-2">
                        <Sketch
                          color={backgroundColor}
                          onChange={(color) => setBackgroundColor(color.hex)}
                        />
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Shape Color */}
              {mode === 'shapes' && selectedShape && (
                <div>
                  <label className="text-sm font-medium text-gray-700 mb-2 block">Shape Color</label>
                  <div className="relative">
                    <button
                      onClick={() => setShowSvgColorPicker(!showSvgColorPicker)}
                      className="w-full h-12 rounded-lg border-2 border-gray-300 relative overflow-hidden group hover:border-gray-400 transition-colors"
                    >
                      <div className="absolute inset-0" style={{ backgroundColor: svgColor }} />
                      <span className="relative z-10 text-sm font-medium mix-blend-difference text-white">
                        {svgColor}
                      </span>
                    </button>

                    {showSvgColorPicker && (
                      <div className="absolute z-20 mt-2">
                        <div className="fixed inset-0" onClick={() => setShowSvgColorPicker(false)} />
                        <div className="relative bg-white rounded-lg shadow-xl p-2">
                          <Sketch
                            color={svgColor}
                            onChange={(color) => setSvgColor(color.hex)}
                          />
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Canvas Area */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-xl shadow-sm p-6">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <h2 className="text-lg font-semibold text-gray-900">Canvas</h2>
                  <p className="text-sm text-gray-500">Click and drag to position</p>
                </div>
                <button
                  onClick={exportAsPNG}
                  disabled={!selectedShape && !uploadedImage}
                  className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2 text-sm font-medium"
                >
                  <Download size={16} />
                  Export PNG
                </button>
              </div>

              {/* Canvas */}
              <div 
                ref={canvasContainerRef}
                className="relative border-2 border-gray-200 rounded-lg overflow-hidden bg-gray-100"
                style={{ paddingBottom: '50%' }}
              >
                <canvas
                  ref={canvasRef}
                  width={CANVAS_WIDTH}
                  height={CANVAS_HEIGHT}
                  className={`absolute inset-0 w-full h-full ${isDragging ? 'cursor-grabbing' : 'cursor-grab'}`}
                  onMouseDown={handleCanvasMouseDown}
                />
              </div>

              {/* Instructions */}
              {!selectedShape && !uploadedImage && (
                <div className="mt-4 text-center py-8">
                  <p className="text-gray-500">Select a shape or upload an image to get started</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
